import loggingfrom datetime import dateimport osimport os.path#logging.basicConfig(filename='ErrorLog.log', level=logging.DEBUG, # format='%(asctime)s %(levelname)s %(name)s %(message)s')#logger=logging.getLogger(__name__)def Error(error):	file_name = "ErrorLog"+date.today().strftime('%Y%m%d') + ".log";	#if not os.path.isfile(os.getcwd() + "\\" + file_name):	logging.basicConfig(filename=file_name, level=logging.ERROR, format='%(asctime)s %(levelname)s %(name)s %(message)s')	logger=logging.getLogger(__name__)	logger.error(error)def CustomLog(file_name, message):	#if not os.path.isfile(os.getcwd() + "\\" + file_name):	logging.basicConfig(filename=file_name, level=logging.WARN, format='%(message)s')	logger=logging.getLogger(__name__)	logger.warning(message)